declare const genUploader: <TRouter extends FileRouter>(initOpts: GenerateUploaderOptions) => <TEndpoint extends keyof TRouter, TSkipPolling extends boolean = false>(endpoint: TEndpoint, opts: Omit<UploadFilesOptions<TRouter, TEndpoint, TSkipPolling>, keyof GenerateUploaderOptions>) => Promise<ClientUploadedFileData<false extends TSkipPolling ? inferEndpointOutput<TRouter[TEndpoint]> : null>[]>;

